import * as React from 'react';
import { Line } from 'react-chartjs-2';
import apiCall from './apiCall';
import movingAverage from './analyseData';

export default class Chart extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
        data: [],
        openData: [],
        labelData: [],
        legend: '',
    };
  }
  componentDidMount() {
      apiCall.call(this.props.uri)
      .then((result) => result.json())
      .then((result) => {
        const openData = result.map((item) => (item.open));
        const labelData = result.map((item) => (item.label));
        this.setState({ data: result,
                        openData: openData,
                        labelData: labelData,
                        legend: this.props.legend
                      });
       })
      .catch((error) => {
         return 'sorry there is an error'
      });
  }
  render() {
    //const movingAverage30 = movingAverage(this.state.openData, 30);
    const data = {
        datasets: [{
        borderColor: [
            '#FF6384'
          ],
        data: this.state.openData,
        label: this.state.legend,
        maintainAspectRatio: true,
        responsive: true,
        },
        // {
        // borderColor: [
        //     '#CCCCB3'
        //   ],
        // data: movingAverage30,
        // //label: this.state.legend,
        // maintainAspectRatio: true,
        // responsive: true,
        // }


    ],
        labels: this.state.labelData
    };
    const options = {
      legend: {
        position: 'bottom',
      }
    };
    return (
       <div style={ { height:'300px', width:'500px' } }>
        <Line data={ data } options={ options }/>
        <button id="delete" onClick = { this.props.delEvent }>delete</button>
       </div>
      )

  }
}
